// @ts-ignore
import React from 'react';
import {Container, Row, Col, Button, Alert, Spinner, Card} from "react-bootstrap";
import useSpeechToText, { ResultType, PictogramasList } from './Hooks';

// @ts-ignore
import micIcon from "./mic.svg";

import './App.css';

export default function App() {
  const {
    error,
    interimResult,
    isRecording,
    isLoading,
    pictogramas,
    sentence,
    results,
    startSpeechToText,
    stopSpeechToText
  } = useSpeechToText({
    continuous: true,
    crossBrowser: true,
    googleApiKey: process.env.REACT_APP_API_KEY,
    speechRecognitionProperties: { interimResults: true },
    useLegacyResults: false
  });

  if (error) {
    return (
      <div
        style={{
          maxWidth: '600px',
          margin: '100px auto',
          textAlign: 'center'
        }}
      >
        <p>
          {error}
          <span style={{ fontSize: '3rem' }}>🤷‍</span>
        </p>
      </div>
    );
  }

  return (
      <Container>
          <Row>
             <Col>&nbsp;</Col>
          </Row>
          <Row>
             <Col md={{ span: 8, offset: 2 }} className="d-grid gap-2">
                 <Button variant="primary" size="lg" onClick={isRecording ? stopSpeechToText : startSpeechToText}>
                    <span>{isRecording ? 'Detener' : 'Comenzar'}</span>
                    <img data-recording={isRecording} src={micIcon} alt="" />
                </Button>
             </Col>
          </Row>
          <Row>
             <Col>&nbsp;</Col>
          </Row>
          <Row>
              <Col>
                  {sentence &&
                  <Alert variant='success'>
                      <b>Pregunta: </b>{sentence} ?
                  </Alert>}

              </Col>
          </Row>
          <Row>
              <Col>
                  {(pictogramas as PictogramasList[]).map((result) => (
                      <li key={result.timestamp}>{result.transcript}</li>
                    ))}
                  <Card style={{ width: '18rem' }}>
                      <Card.Img variant="top" src="holder.js/100px180" />
                      <Card.Body>
                        <Card.Title>Card Title</Card.Title>
                        <Card.Text>
                          Some quick example text to build on the card title and make up the bulk of
                          the card's content.
                        </Card.Text>
                        <Button variant="primary">Go somewhere</Button>
                      </Card.Body>
                    </Card>
              </Col>
          </Row>

          {isLoading && <div style={{ position: 'absolute', width: '100%', height:'100%', top: '0', alignItems: "center", justifyContent: "center"}}>
              <Spinner animation="border" variant="success"/>
          </div>
          }
      </Container>

  );
}
