// @ts-ignore
import React from 'react';
import {Container, Row, Col, Button} from "react-bootstrap";
import useSpeechToText, { ResultType } from './Hooks';

import micIcon from './mic.svg';

import './App.css';

export default function App() {
  const {
    error,
    interimResult,
    isRecording,
    sentence,
    results,
    startSpeechToText,
    stopSpeechToText
  } = useSpeechToText({
    continuous: true,
    crossBrowser: true,
    googleApiKey: process.env.REACT_APP_API_KEY,
    speechRecognitionProperties: { interimResults: true },
    useLegacyResults: false
  });

  if (error) {
    return (
      <div
        style={{
          maxWidth: '600px',
          margin: '100px auto',
          textAlign: 'center'
        }}
      >
        <p>
          {error}
          <span style={{ fontSize: '3rem' }}>ü§∑‚Äç</span>
        </p>
      </div>
    );
  }

  return (
      <Container>
          <Row>
             <Col>&nbsp;</Col>
          </Row>
          <Row>
             <Col md={{ span: 8, offset: 2 }} className="d-grid gap-2">
                 <Button variant="primary" size="lg" onClick={isRecording ? stopSpeechToText : startSpeechToText}>
                    <span>{isRecording ? 'Detener' : 'Comenzar'}</span>
                    <img data-recording={isRecording} src={micIcon} alt="" />
                </Button>
             </Col>
          </Row>
          `{sentence}`
      </Container>

  );
}
